import { Component, OnInit } from '@angular/core';
import { Survey } from 'src/app/models/survey';
import { SurveysService } from 'src/app/services/surveys.service';

@Component({
  selector: 'app-form',
  templateUrl: './form.page.html',
  styleUrls: ['./form.page.scss'],
})
export class FormPage implements OnInit {
  public surveysList : Survey[] = [];
  public surveyBeingCreated : Survey = {
    name: "",
    birthday: "",
    description: ""
  };
  public isModalOpen : boolean = false;

  constructor(private surveysService : SurveysService) { }

  ngOnInit() {
    this.loadSurveys();
  }
  public loadSurveys(){
    this.surveysService.surveysList().then(list=>{
      this.surveysList = list;
    })

  }
  public showSurveyForm() {
    this.surveyBeingCreated = {
      name: "",
      birthday: "",
      description: ""
    };
    this.isModalOpen = true;
  }
  public cancel() {
    this.isModalOpen = false;
  }
  public save() {
    this.surveysService.saveSurvey(this.surveyBeingCreated);
    this.loadSurveys();
    this.isModalOpen = false;
  }

}
<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-buttons slot="start">
      <ion-menu-button></ion-menu-button>
    </ion-buttons>
    <ion-title>survey forms</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">

  <div *ngIf="surveysList.length !== 0; else elseBlock">
     <ion-card *ngFor="let survey of surveysList; let index">
        <ion-card-header>
          <ion-card-title>{{ survey.name }}</ion-card-title>
          <ion-card-subtitle>{{ survey.birthday }}</ion-card-subtitle>
        </ion-card-header>
        <ion-card-content>
          {{ survey.description }}
        </ion-card-content>
     </ion-card>

  </div>
  <ng-template #elseBlock>
    <div class="ion-text-center">
      <h3>No surveys !</h3>
      you not have registered survey. you can use the floating button below to add some.
    </div>
  </ng-template>

</ion-content>
