      <ion-menu-button></ion-menu-button>
    </ion-buttons>
    <ion-title>bible</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true" *ngFor="let i to bibleData.verse">

  ---------------------------
    {{bibleData.text}}


</ion-content>
