import { Component, OnInit } from '@angular/core';
import { Photo} from '@capacitor/camera'
import { PhotoService } from 'src/app/services/photo.service';

@Component({
  selector: 'app-picture',
  templateUrl: './picture.page.html',
  styleUrls: ['./picture.page.scss'],
})
export class PicturePage implements OnInit {
  public photo: Photo | undefined = undefined;

  constructor(private photoService: PhotoService) { }
  addPhotoToGallery() {
    this.photoService.addNewToGallery();
  }

  ngOnInit() {
  }


}
<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-buttons slot="start">
      <ion-menu-button></ion-menu-button>
    </ion-buttons>
    <ion-title>Take a picture</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <div class="ion-padding">
    <ion-button expand="block" (click)="capture()">Click to capture</ion-button> 
    <ion-grid *ngIf="photo !==undefined">
      <ion-row>
        <ion-col size="12">
          <ion-img [src]="photo.webPath"></ion-img>
        </ion-col>
      </ion-row>

    </ion-grid>
  </div>
</ion-content>
