import { HttpClient} from '@angular/common/http';
import { Component, OnInit} from '@angular/core';
import { Observable } from 'rxjs';



@Component({
  selector: 'app-bible',
  templateUrl: './bible.page.html',
  styleUrls: ['./bible.page.scss'],
})
export class BiblePage implements OnInit {

  constructor(private http:HttpClient) {}

  bibleData: any= [];
   runHttp(){
    this.http.get('https://bible-api.com/john 3:16')
    .subscribe(data => {
      console.log(data);
      this.bibleData=data;
      console.log(this.bibleData['reference']);
    });

   }

  ngOnInit() {
    this.runHttp();
  }

}
<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-buttons slot="start">
      <ion-menu-button></ion-menu-button>
    </ion-buttons>
    <ion-title>bible</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <div>
    <ion-card >
      <ion-card-header>
        <ion-card-title>{{bibleData.reference}}</ion-card-title>
        <ion-card-subtitle *ngFor="let bible of bibleData">{{bible.verses}}</ion-card-subtitle>
      </ion-card-header>
    </ion-card>

  </div>
</ion-content>
